mapper.insert:
/* RentMapper.insertRent */
SELECT UUID() AS RENT_UID
INSERT INTO tb_rent ( RENT_UID, RENT_TITLE, RENT_ST_DT, RENT_EN_DT, DEL_YN, CREATE_USER_ID, CREATE_DT, UPDATE_USER_ID, UPDATE_DT ) VALUES ( #{rentUid}, #{rentTitle}, #{rentStDt}, #{rentEnDt}, 'N', #{createUserId}, SYSDATE(), #{updateUserId}, SYSDATE() )

mapper.update:
/* RentMapper.deleteRent */
UPDATE tb_rent SET
    DEL_YN = 'Y',
    UPDATE_USER_ID = #{updateUserId},
    UPDATE_DT = SYSDATE()
WHERE RENT_UID = #{rentUid}
