mapper.insert:
/* TournamentMapper.insertTournament */
SELECT UUID() AS TOURNAMENT_UID
INSERT INTO tb_tournament ( TOURNAMENT_UID, TOURNAMENT_TITLE, TOURNAMENT_ST_DT, TOURNAMENT_EN_DT, ORI_FILE_NM, NEW_FILE_NM, FULL_FILE_NM, DEL_YN, CREATE_USER_ID, CREATE_DT, UPDATE_USER_ID, UPDATE_DT ) VALUES ( #{tournamentUid}, #{tournamentTitle}, #{tournamentStDt}, #{tournamentEnDt}, #{oriFileNm}, #{newFileNm}, #{fullFileNm}, 'N', #{createUserId}, SYSDATE(), #{updateUserId}, SYSDATE() )

mapper.update:
/* TournamentMapper.deleteTournament */
UPDATE tb_tournament SET
    DEL_YN = 'Y',
    UPDATE_USER_ID = #{updateUserId},
    UPDATE_DT = SYSDATE()
WHERE TOURNAMENT_UID = #{tournamentUid}
